$(document).ready(function(){$(".comment-form").validate({errorClass:"invalid",errorElement:"em",showErrors:function(a,b){b.length>0&&($(".alert.issue").html("<h3>Couldn't submit comment</h3><p>There are "+this.numberOfInvalids()+" errors, please see below and fix.</p>").show(),this.defaultShowErrors(),$(".alert.success").hide())},highlight:function(a){$(a).parent(".field").parent(".row").addClass("invalid")},unhighlight:function(a){$(a).parent(".field").parent(".row").removeClass("invalid")},rules:{name:"required",email:{required:!0,email:!0},comment:"required"},messages:{name:"Please enter your name.",email:"Please enter a valid email address.",comment:"Please start the discussion."}})});