$(document).ready(function(){$(".contact-form").validate({errorClass:"invalid",errorElement:"em",showErrors:function(a,b){b.length>0&&($(".alert.issue").html("<h3>Couldn't send email</h3><p>There are "+this.numberOfInvalids()+" errors, please see below and fix.</p>").show(),this.defaultShowErrors(),$(".alert.success").hide())},highlight:function(a){$(a).parent(".field").parent(".row").addClass("invalid")},unhighlight:function(a){$(a).parent(".field").parent(".row").removeClass("invalid")},rules:{from:{required:!0,email:!0},message:"required"},messages:{from:"Please enter a valid email address.",message:"Please start the discussion."}})});